import React, { Component } from 'react'
import '../App.css';

export default class AutoCompleteText extends Component {
  constructor(props){
      super(props);
    this.items = ["Fear",
    "Angry",
    "Sad",
    "Disconnected",
    "Disoriented",
    "Unreal",
    "Jittery",
    "Anxious",
    "Depressed"

    ];
    this.state={
        suggestions:[],
        text:""
    };
  }

  onTextChanged = (e) =>{
    const value = e.target.value; 
    let suggestions = [];
    if (value.length >0){
        const regex = new RegExp(`^${value}`, 'i');
        suggestions = this.items.sort().filter(v => regex.test(v));
    }
        this.setState(() => ({suggestions, text:value}));

    }

suggestionSelected(value){
    this.setState(() =>({
        text:value,
        suggeestion:[]
    }))
}


onSubmit =(e)=> {
    e.preventDefault();
    this.props.update(this.state.id,this.state.title, this.state.type, this.state.location, this.state.date);
    this.setState({id: ' '});
    this.setState({title: ' '});
    this.setState({type: ' '});
    this.setState({location: ' '});
    this.setState({date: ' '});
  }
  componentDidUpdate(prevProps){
    if (this.props.event!==prevProps.event){
      this.setState({...this.props.event})
    }
  }

    renderSuggestions(){
        const {suggestions} = this.state;
        if (suggestions.length === 0 ){
            return null
        }
        return (
            <ul>
                {suggestions.map((item) => <li onClick={()=> this.suggestionSelected(item)}>{item}</li>)}
            </ul>
        )
    }


    render() {
        const { text } = this.state
    return (
        <div>
            <h1>I am feeling</h1>
      <div className="AutoCompleteText">
        <input value={text} type="text" onChange={this.onTextChanged} onSubmit={this.onSubmit}/>
        <ul>
            {this.renderSuggestions()}
        </ul>
      </div>
      <p className='flagText'>Thoughs of suicide</p>
      </div>
    )
  }
}
