import React, { Component } from 'react';
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider'

import './App.css';
import { BrowserRouter as Router, Route, Link} from 'react-router-dom';

import SkillsList from './components/SkillsList';
import Search from './components/Search/Search';
import ImageResults from './components/Image-Results/ImageResults';
import AutoCompleteText from './components/AutoCompleteText';

import pinata_big from './components/img/pinata_big.png';



const http = require('http');
const fetch = require('node-fetch');

class App extends Component {
state ={ 
  skillsList:[],
    user_id:'2',
    record_id:'',
    before_lvl:'',
    after_lvl:'',
    impact:'',
    emotion:'Angry',
    skill:'',
    si:'',
    sh:'',
    recordsArray : [],
    userSkillsArray : [],
    emotionSkillsArray : [],
    baseSkillsArray : [],
    skill_1 : {},
    skill_2 : {},
    skill_3 : {},
    skill_4 : {},
    skill_5 : {},
    skill_6 : {},
    skill_7 : {},
    skill_8 : {}

}

getUserSkills = () => {

  let url = `http://localhost:3001/api/userSkills?id=${this.state.user_id}&emotion=${this.state.emotion}`

  fetch(url, {
    method: 'get',
    headers: { 'Content-Type': 'application/json'}
    })
    .then(res => res.json()).then(json => this.setState({userSkillsArray: json})).catch(function(e) {
    console.log(e); // “oh, no!”
   })

}

getEmotionSkills = () => {
  fetch("http://localhost:3001/api/emotionSkills", {
    method: 'get',
    headers: { 'Content-Type': 'application/json'}
    })
    .then(res => res.json()).then(json => this.setState({skillsList: json})).catch(function(e) {
    console.log(e); // “oh, no!”
   })

}

// server not written 
// getBaseSkills = ()=>{
//   fetch("http://localhost:3001/api/skills", {
//     method: 'get',
//     headers: { 'Content-Type': 'application/json'}
//     })
//     .then(res => res.json()).then(json => this.setState({skillsList: json})).catch(function(e) {
//     console.log(e); // “oh, no!”
//    })

//   }


  render() {
    return (

      <MuiThemeProvider>
        <Router>
          <div className="App">
          {/* <NavBar/> */}



          <Route path="/" exact render = { props =>(
            <React.Fragment>
          <header className="App-header">

          <h1>Piñata</h1>
          <Link to ='/feeling'>
          <img style={{"padding":"50px", "width":"35%"}} src={pinata_big} alt={'pinata'}/>
          </Link>

          </header>
          </React.Fragment>)} />

          <Route path="/feeling" exact render = { props =>(
            <React.Fragment>
              <div className="App-Component">
                <AutoCompleteText />

              </div>
          </React.Fragment>)} />


          <Route exact path="/grid" exact render = { props =>(
            <React.Fragment>

          <h1>welcome to the skills grid </h1>  

            </React.Fragment>)} />



          <Route exact path="/skills" exact render = { props =>(
            <React.Fragment>


              <Search/>

              <SkillsList  skillsList= {this.state.skillsList} /> 
              <ImageResults />   

            </React.Fragment>)} />




        </div>
        </Router>
        </MuiThemeProvider>
    );
  }
}

export default App;
