import React, { Component } from 'react';
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider'
import logo from './logo.svg';
import './App.css';
import { BrowserRouter as Router, Route, Link} from 'react-router-dom';
import GetAllSkills from './components/GetAllSkills.js';
import Skill from './components/Skill.js';
import SkillsList from './components/SkillsList.js';
import music from './components/img/music.png';
import NavBar from './components/NavBar/NavBar';

const http = require('http');
const fetch = require('node-fetch');

class App extends Component {
state ={ 
  skillsList:[],
  postFeeling:'',
  preFeeling:'',

  // resultSkills:[],
  // selectedSkills:null
}

getAllSkills = ()=>{
  fetch("http://localhost:3001/api/skills", {
    method: 'get',
    headers: { 'Content-Type': 'application/json'}
    })
    .then(res => res.json()).then(json => this.setState({skillsList: json})).catch(function(e) {
    console.log(e); // “oh, no!”
   })

  }


  render() {
    return (

      <MuiThemeProvider>
        <Router>
          <div className="App">
          <NavBar/>

          <Route path="/" exact render = { props =>(
            <React.Fragment>
          <header className="App-header">
          {/* <img src={aikido} style={{ backgroundColor: 'transparent'}} /> */}

          <h1>Piñata</h1>

          </header>
          </React.Fragment>)} />


          <Route exact path="/skills" render = { props =>(
            <React.Fragment>

              <h2>List of Skills</h2>
              <NavBar/>
              <GetAllSkills getAllSkills={this.getAllSkills}/>
              <SkillsList  skillsList= {this.state.skillsList} />    

            </React.Fragment>)} />

          {/* <Route path="/quotes/:id" exact component = {getQuoteById}/> */}


        </div>
        </Router>
        </MuiThemeProvider>
    );
  }
}

export default App;
